package com.jibestream.androidsdk.customElements;

import android.content.Context;
import android.util.Log;

import com.jibestream.jibestreamandroidlibrary.elements.ElementMap;
import com.jibestream.jibestreamandroidlibrary.main.Camera;
import com.jibestream.jibestreamandroidlibrary.main.M;
import com.jibestream.jibestreamandroidlibrary.shapes.Rectangle;

/**
 * Created by emmanuel on 15-05-13.
 */
public class Child extends ElementMap {
public Child() {
  final Rectangle rectangle = new Rectangle(0, 0, 30, 10);
  setShape(rectangle);
}

@Override
public void onCreate(Context context, M m, long timeElapsed, long timeTotal, Camera camera) {
  super.onCreate(context, m, timeElapsed, timeTotal, camera);
  getTransform().setTranslationX(30f);
  getTransform().setTranslationY(30f);
  getTransform().setTranslationOffsetX(-15f);
  getTransform().setTranslationOffsetY(-5f);
}

@Override
public void onUpdate(M m, long timeElapsed, long timeTotal, int fps, Camera camera) {
  super.onUpdate(m, timeElapsed, timeTotal, fps, camera);
  getTransform().setRotation(timeTotal * 0.10f);
  getTransform().setScale((float) ((Math.sin(timeTotal * 0.001f) + 2f) / 2f * 0.4f)/60f);
}

}
