package com.jibestream.androidsdk.customElements;

import android.graphics.Paint;
import android.util.Log;

import com.jibestream.jibestreamandroidlibrary.elements.StreetMajor;
import com.jibestream.jibestreamandroidlibrary.intents.IntentTouch;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

/**
 * Created by jibestream on 2016-07-27.
 */
public class CustomRoad extends StreetMajor {

private static final RenderStyle styleSelected = new RenderStyle(Paint.Style.STROKE);


static {
  styleSelected.paintStroke.setColor(ColorsMaterialDesign.AMBER9);
  styleSelected.setStrokeWidth(15);
}

public CustomRoad() {
  setSelectable(true);
  setStyleSelected(styleSelected);
}

@Override
public void onTouchEvent(IntentTouch intentTouch) {
  super.onTouchEvent(intentTouch);
  setSelectState(!isSelectState());
  Log.d(TAG, "onTouchEvent: hello");
}
}
