package com.jibestream.androidsdk.customElements;

import com.jibestream.jibestreamandroidlibrary.main.Camera;
import com.jibestream.jibestreamandroidlibrary.main.M;
import com.jibestream.jibestreamandroidlibrary.shapes.IShape;

/**
 * Created by emmanuel on 2015-10-25.
 */
public class MoverHead extends com.jibestream.jibestreamandroidlibrary.elements.MoverHead {
public MoverHead() {
}

@Override
public synchronized void setShape(IShape iShape) {
  super.setShape(iShape);
  if (iShape == null) return;
  setOffsetY(iShape.getBBox().height() / -2f);
}

@Override
public void onUpdate(M m, long timeElapsed, long timeTotal, int fps, Camera camera) {
  super.onUpdate(m, timeElapsed, timeTotal, fps, camera);
  getTransform().setScale((float) Math.cos(timeTotal * 0.007f) * 0.07f + 0.4f);

}
}
