package com.jibestream.androidsdk.customElements;

import android.content.Context;
import android.graphics.Paint;

import com.jibestream.jibestreamandroidlibrary.elements.ElementCustom;
import com.jibestream.jibestreamandroidlibrary.main.Camera;
import com.jibestream.jibestreamandroidlibrary.main.M;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

/**
 * Created by jibestream on 2016-08-02.
 */
public class MyElementCustom extends ElementCustom {

@Override
public void onCreate(Context context, M m, long timeElapsed, long timeTotal, Camera camera) {
  super.onCreate(context, m, timeElapsed, timeTotal, camera);
  RenderStyle elementCustomStyle = new RenderStyle(Paint.Style.FILL_AND_STROKE);
  elementCustomStyle.paintFill.setColor(ColorsMaterialDesign.RED);
  elementCustomStyle.paintStroke.setColor(ColorsMaterialDesign.BLUE1);
  elementCustomStyle.paintStroke.setStrokeWidth(5);
  elementCustomStyle.paintStroke.setAntiAlias(true);
  setStyleIdle(elementCustomStyle);
}

}
