package com.jibestream.androidsdk.customElements;

import android.graphics.Paint;
import android.graphics.RectF;

import com.jibestream.androidsdk.customShape.DottedPathShape;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

/**
 * Created by emmanuel on 15-08-28.
 */
public class Route extends com.jibestream.jibestreamandroidlibrary.elements.Route {
public Route() {
  super();
  setShape(new DottedPathShape());

//  setBoundingBoxVisible(true);

  RenderStyle pathRenderStyle = new RenderStyle(Paint.Style.FILL_AND_STROKE);
  pathRenderStyle.paintFill.setColor(ColorsMaterialDesign.GREY1);
  pathRenderStyle.setStrokeColor(ColorsMaterialDesign.GREY7);
  pathRenderStyle.setStrokeWidth(0.5f);

  setStyleIdle(pathRenderStyle);
}
}
