package com.jibestream.androidsdk.customElements;

import android.graphics.Paint;

import com.jibestream.jibestreamandroidlibrary.shapes.Star;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

/**
 * Created by emmanuel on 15-08-29.
 */
public class WayfindKiosk extends com.jibestream.jibestreamandroidlibrary.elements.WayfindKiosk {
public WayfindKiosk() {
  // Define a new drawing shape for this element
  setShape(new Star(3.5f, 3, 8));

  // Create a new style
  final RenderStyle renderStyle = new RenderStyle(Paint.Style.FILL);
  renderStyle.paintFill.setColor(ColorsMaterialDesign.RED5);
  setStyleIdle(renderStyle);
}
}
