public class Amenity extends ElementIcon {
public com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Amenity amenityComponent;
public Waypoint waypoint;

public Amenity() {
  setSelectable(true);
  setHighlightable(true);
//  setConstantScale(true);
  setHeadsUp(true);
//  getTransform().setScaleOffset(2.5f);

  renderStyleIconIdle = new RenderStyleIcon();
  renderStyleIconIdle.renderStyleBG.paintFill.setColor(ColorsMaterialDesign.GREY9);
  renderStyleIconIdle.renderStyleMG.paintFill.setColor(ColorsMaterialDesign.GREY6);
  renderStyleIconIdle.renderStyleFG.paintFill.setColor(ColorsMaterialDesign.GREY3);

  renderStyleIconSelected = new RenderStyleIcon();
  renderStyleIconSelected.renderStyleBG.paintFill.setColor(ColorsMaterialDesign.GREY3);
  renderStyleIconSelected.renderStyleMG.paintFill.setColor(ColorsMaterialDesign.GREY6);
  renderStyleIconSelected.renderStyleFG.paintFill.setColor(ColorsMaterialDesign.GREY9);
}

}
