package com.jibestream.jibestreamandroidlibrary.elements;

import android.graphics.Canvas;
import android.graphics.Paint;
import android.graphics.RectF;

/**
 * Created by emmanuel on 15-07-05.
 */
public class Background extends ElementMap {
public final RectF bbox = new RectF(0, 0, Integer.MAX_VALUE, Integer.MAX_VALUE);

public Background() {
  super();
  setUpdateInterval(0);
}

@Override
public RectF getBBox() {
  return bbox;
}

@Override
public void onRender(Canvas canvas, Paint touchPaint) {
//  canvas.drawColor(Color.TRANSPARENT, PorterDuff.Mode.CLEAR);
//  canvas.drawColor(Color.WHITE);
  canvas.drawColor(getStyleIdle().paintFill.getColor());
}
}
