package com.jibestream.jibestreamandroidlibrary.elements;

import android.graphics.Bitmap;
import android.graphics.Canvas;
import android.graphics.Paint;

import com.jibestream.jibestreamandroidlibrary.shapes.IconShape;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.shapes.Image;
import com.jibestream.jibestreamandroidlibrary.shapes.Text;
import com.jibestream.jibestreamandroidlibrary.utils.Ninegrid;

/**
 * Created by emmanuel on 15-07-15.
 */
public class DestinationLabel extends Element {
private String textString;
private final Image image;
private final Text text;
private IconShape iconShape;
private final RenderStyle styleIdle;

public DestinationLabel() {
  super();
  text = new Text("Label");
  image = new Image(null, 20, 20, new Ninegrid(Ninegrid.MiddleMiddle));
  styleIdle = new RenderStyle(Paint.Style.FILL);
  styleIdle.paintFill.setTextAlign(Paint.Align.CENTER);
  styleIdle.paintFill.setTextSize(15);

  setUpdateInterval(0);
  setHeadsUp(true);
}

@Override
public void onRender(Canvas canvas, Paint paint) {
//  setShape(text);
//  super.onRender(canvas, camera.gtMatrix(), paint);
  setShape(image);
  super.onRender(canvas, paint);
//  image.onDraw(canvas,styleIdle.paintFill);
}

public void setLabelName(String labelName) {
  textString = labelName;
  text.setTextString(labelName);
}

public void setBitmap(Bitmap bitmap) {
  image.setBitmap(bitmap);
}

public void setIconShape(IconShape iconShape) {
  this.iconShape = iconShape;
}

}
