package com.jibestream.jibestreamandroidlibrary.elements;

import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Destination;
import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Waypoint;

/**
 * Elements that have been preprocessed on the backend should implement this interface
 * Unit and LBox classes currently implement it
 */
public interface IPreProcessedElem {

int[] getDestinationIDs();

void setDestinationIDs(int[] destinationIDs);

int[] getWaypointIDs();

void setWaypointIDs(int[] waypointIDs);

Destination[] getDestinations();

void setDestinations(Destination[] Destinations);

Waypoint[] getWaypoints();

void setWaypoints(Waypoint[] waypoints);
}
