package com.jibestream.jibestreamandroidlibrary.elements;

import com.jibestream.jibestreamandroidlibrary.styles.RenderStyleIcon;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

import java.util.Arrays;

/**
 * Created by emmanuel on 15-08-27.
 */
public class Mover extends ElementIcon {
public static final int HEAD = 0;
public static final int TAIL = 1;
private int moverType;

public Mover() {

  RenderStyleIcon renderStyleIconIdle = new RenderStyleIcon();
  renderStyleIconIdle.renderStyleBG.paintFill.setColor(ColorsMaterialDesign.GREY9);
  renderStyleIconIdle.renderStyleMG.paintFill.setColor(ColorsMaterialDesign.REDJS);
  renderStyleIconIdle.renderStyleFG.paintFill.setColor(ColorsMaterialDesign.GREY3);

  setVisible(false);
  setHeadsUp(true);
}

public int getMoverType() {
  return moverType;
}

public void setMoverType(int moverType) {
  this.moverType = moverType;
}

public void setMoverTypeHead() {
  moverType = HEAD;
}

public void setMoverTypeTail() {
  moverType = TAIL;
}
}
