package com.jibestream.jibestreamandroidlibrary.elements;

import android.content.Context;
import android.graphics.Paint;

import com.jibestream.jibestreamandroidlibrary.main.Camera;
import com.jibestream.jibestreamandroidlibrary.main.M;
import com.jibestream.jibestreamandroidlibrary.shapes.IShape;
import com.jibestream.jibestreamandroidlibrary.shapes.Image;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.Ninegrid;

/**
 * Created by emmanuel on 15-08-27.
 */
public class Pin extends ElementMap {
public Pin() {
  setHeadsUp(true);
  setVisible(false);
}

@Override
public void onCreate(Context context, M m, long timeElapsed, long timeTotal, Camera camera) {
  super.onCreate(context, m, timeElapsed, timeTotal, camera);

  final IShape shape = getShape();
  if (shape == null ){
    Image image = new Image(
        null,
        60, 60,
        new Ninegrid(Ninegrid.BottomMiddle)
    );

    styleIdle = new RenderStyle(Paint.Style.FILL);
    styleIdle.paintFill.setFilterBitmap(true);
    styleIdle.paintFill.setAntiAlias(true);
    image.setBitmap(m.bitmapLib.pin);

    setShape(image);
  }
}
}
