package com.jibestream.jibestreamandroidlibrary.elements;

import android.graphics.Paint;
import android.graphics.RectF;

import com.jibestream.jibestreamandroidlibrary.shapes.IShape;
import com.jibestream.jibestreamandroidlibrary.shapes.JPathAnimatable;
import com.jibestream.jibestreamandroidlibrary.styles.RenderStyle;
import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;


/**
 * Created by emmanuel on 15-06-20.
 */
public class Route extends ElementMap {
private static final String TAG = "Route";

public Route() {
  super();

  RenderStyle pathRenderStyle;
  pathRenderStyle = new RenderStyle(Paint.Style.FILL);
  pathRenderStyle.paintFill.setColor(ColorsMaterialDesign.WHITE);
  setStyleIdle(pathRenderStyle);

  setShape(new JPathAnimatable());
}

// force updating dirty region
@Override
public boolean getDirtyStyle() {
  return true;
}

//return shape path since in global space
@Override
public RectF getBBox() {
  final IShape shape = getShape();
  if (shape ==null) return null;
  return shape.getBBox();
}
}