package com.jibestream.jibestreamandroidlibrary.http;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 2015-11-09.
 */

public class BasicAuthentication implements Parcelable {
  public static final String USER_KEY = "x-jsapi_user";
  public static final String PASSCODE_KEY = "x-jsapi_passcode";
  public static final String API_KEY = "x-jsapi_key";
  public static final String LANGUAGE_CODE_KEY = "x-lcode";

  public String userValue;
  public String passcodeValue;
  public String apiValue;
  public String languageCodeValue;
  public String versionValue;

  public BasicAuthentication(String userValue, String passcodeValue, String apiValue, String languageCodeValue) {
    this.userValue = userValue;
    this.passcodeValue = passcodeValue;
    this.apiValue = apiValue;
    this.languageCodeValue = languageCodeValue;
  }

  @Override
  public int describeContents() {
    return 0;
  }

  @Override
  public void writeToParcel(Parcel dest, int flags) {
    dest.writeString(this.userValue);
    dest.writeString(this.passcodeValue);
    dest.writeString(this.apiValue);
    dest.writeString(this.languageCodeValue);
    dest.writeString(this.versionValue);
  }

  protected BasicAuthentication(Parcel in) {
    this.userValue = in.readString();
    this.passcodeValue = in.readString();
    this.apiValue = in.readString();
    this.languageCodeValue = in.readString();
    this.versionValue = in.readString();
  }

  public static final Parcelable.Creator<BasicAuthentication> CREATOR = new Parcelable.Creator<BasicAuthentication>() {
    public BasicAuthentication createFromParcel(Parcel source) {
      return new BasicAuthentication(source);
    }

    public BasicAuthentication[] newArray(int size) {
      return new BasicAuthentication[size];
    }
  };
}
