package com.jibestream.jibestreamandroidlibrary.intents;

import android.annotation.SuppressLint;
import android.content.Intent;
import android.os.Parcel;

/**
 * Created by emmanuel on 2015-12-14.
 */
@SuppressLint("ParcelCreator")
public class IntentActiveness extends Intent {
public int mID;

public IntentActiveness(int mID, String action) {
  super(action);
  this.mID = mID;
}

public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
}

/**
 * @hide
 */
protected IntentActiveness(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
}

public static final Creator<IntentActiveness> CREATOR = new Creator<IntentActiveness>() {
  public IntentActiveness createFromParcel(Parcel source) {
    return new IntentActiveness(source);
  }

  public IntentActiveness[] newArray(int size) {
    return new IntentActiveness[size];
  }
};

}
