package com.jibestream.jibestreamandroidlibrary.intents;

import android.content.Intent;
import android.os.Parcel;

import com.jibestream.jibestreamandroidlibrary.intentFilters.IntentFilterAmenities;
import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Amenity;

/**
 * Created by emmanuel on 2015-12-14.
 */
public class IntentAmenities extends Intent {
public int mID;
public Amenity amenity;
public int index;
public boolean oldValue;
public boolean newValue;

public IntentAmenities(int mID, Amenity amenity, int index, boolean oldValue, boolean newValue) {
  super(IntentFilterAmenities.ACTION);
  this.mID = mID;
  this.amenity = amenity;
  this.index = index;
  this.oldValue = oldValue;
  this.newValue = newValue;
}

public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
  dest.writeParcelable(amenity, 0);
  dest.writeInt(index);
  dest.writeByte((byte) (oldValue ? 1 : 0));
  dest.writeByte((byte) (newValue ? 1 : 0));
}

/**
 * @hide
 */
protected IntentAmenities(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
  amenity = in.readParcelable(Amenity.class.getClassLoader());
  index = in.readInt();
  oldValue = in.readByte() != 0;
  newValue = in.readByte() != 0;
}

public static final Creator<IntentAmenities> CREATOR = new Creator<IntentAmenities>() {
  public IntentAmenities createFromParcel(Parcel source) {
    return new IntentAmenities(source);
  }

  public IntentAmenities[] newArray(int size) {
    return new IntentAmenities[size];
  }
};
}
