package com.jibestream.jibestreamandroidlibrary.intents;

import android.annotation.SuppressLint;
import android.content.Intent;
import android.os.Parcel;

/**
 * Created by emmanuel on 2015-12-14.
 */
@SuppressLint("ParcelCreator")
public class IntentEngine extends Intent {
public int mID;

public IntentEngine(int mID, String state) {
  super( state);
  this.mID = mID;
}

public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
}

/**
 * @hide
 */
protected IntentEngine(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
}

public static final Creator<IntentEngine> CREATOR = new Creator<IntentEngine>() {
  public IntentEngine createFromParcel(Parcel source) {
    return new IntentEngine(source);
  }

  public IntentEngine[] newArray(int size) {
    return new IntentEngine[size];
  }
};

}
