package com.jibestream.jibestreamandroidlibrary.intents;

import android.content.Intent;
import android.os.Parcel;

import com.jibestream.jibestreamandroidlibrary.intentFilters.IntentFilterMap;

/**
 * Created by emmanuel on 2015-12-14.
 */
public class IntentMap extends Intent {
public int mID;
public int indexCurrent;
public int indexPrevious;

public IntentMap(int mID, int indexCurrent, int indexPrevious) {
  super(IntentFilterMap.ACTION);
  this.mID = mID;
  this.indexCurrent = indexCurrent;
  this.indexPrevious = indexPrevious;
}

public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
  dest.writeInt(indexCurrent);
  dest.writeInt(indexPrevious);
}

/**
 * @hide
 */
protected IntentMap(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
  indexCurrent = in.readInt();
  indexPrevious = in.readInt();
}

public static final Creator<IntentMap> CREATOR = new Creator<IntentMap>() {
  public IntentMap createFromParcel(Parcel source) {
    return new IntentMap(source);
  }

  public IntentMap[] newArray(int size) {
    return new IntentMap[size];
  }
};
}
