package com.jibestream.jibestreamandroidlibrary.intents;

import android.content.Intent;
import android.os.Parcel;

import com.jibestream.jibestreamandroidlibrary.elements.Element;

/**
 * Created by emmanuel on 2015-12-14.
 */
public class IntentTouch extends Intent {
public int mID;
public int id;
public Element element;

public IntentTouch(int mID, String action, int id, Element element) {
  super(action);
  this.mID = mID;
  this.id = id;
  this.element = element;
}

public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
  dest.writeInt(id);
}

/**
 * @hide
 */
protected IntentTouch(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
  id = in.readInt();
}

public static final Creator<IntentTouch> CREATOR = new Creator<IntentTouch>() {
  public IntentTouch createFromParcel(Parcel source) {
    return new IntentTouch(source);
  }

  public IntentTouch[] newArray(int size) {
    return new IntentTouch[size];
  }
};
}
