package com.jibestream.jibestreamandroidlibrary.intents;

import android.content.Intent;
import android.os.Parcel;

import com.jibestream.jibestreamandroidlibrary.intentFilters.IntentFilterWayfind;
import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.astar.PathPerFloor;

/**
 * Created by emmanuel on 2015-12-14.
 */
public class IntentWayfind extends Intent {
public PathPerFloor[] pathPerFloors;
public int mID;

public IntentWayfind(int mID,PathPerFloor[] pathPerFloors) {
  super( IntentFilterWayfind.ACTION);
  this.mID = mID;
  this.pathPerFloors = pathPerFloors;
}
public void writeToParcel(Parcel dest, int parcelableFlags) {
  super.writeToParcel(dest, parcelableFlags);
  dest.writeInt(mID);
}

/**
 * @hide
 */
protected IntentWayfind(Parcel in) {
  readFromParcel(in);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
}

public static final Creator<IntentWayfind> CREATOR = new Creator<IntentWayfind>() {
  public IntentWayfind createFromParcel(Parcel source) {
    return new IntentWayfind(source);
  }

  public IntentWayfind[] newArray(int size) {
    return new IntentWayfind[size];
  }
};
}
