package com.jibestream.jibestreamandroidlibrary.intents;

import android.content.Intent;
import android.os.Parcel;

import com.jibestream.jibestreamandroidlibrary.intentFilters.IntentFilterWaypoint;
import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Waypoint;

/**
 * Created by emmanuel on 2015-12-14.
 */
public class IntentWaypoint extends Intent {
public int mID;
public Waypoint from;
public Waypoint to;
public int accessLevel;


public IntentWaypoint(int mID, Waypoint from, Waypoint to, int accessLevel) {
  super(IntentFilterWaypoint.ACTION);
  this.mID = mID;
  this.from = from;
  this.to = to;
  this.accessLevel = accessLevel;
}

private IntentWaypoint(Parcel in) {
  readFromParcel(in);
}

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  super.writeToParcel(dest, flags);
  dest.writeInt(mID);
  dest.writeParcelable(from, 0);
  dest.writeParcelable(to, 0);
  dest.writeInt(accessLevel);
}

public void readFromParcel(Parcel in) {
  super.readFromParcel(in);
  mID = in.readInt();
  from = in.readParcelable(Waypoint.class.getClassLoader());
  to = in.readParcelable(Waypoint.class.getClassLoader());
  accessLevel = in.readInt();
}


public static final Creator<IntentWaypoint> CREATOR = new Creator<IntentWaypoint>() {
  public IntentWaypoint createFromParcel(Parcel source) {
    return new IntentWaypoint(source);
  }

  public IntentWaypoint[] newArray(int size) {
    return new IntentWaypoint[size];
  }
};
}
