package com.jibestream.jibestreamandroidlibrary.main;

import android.util.Log;

import com.jibestream.jibestreamandroidlibrary.shapes.IconShape;

import java.util.HashMap;
import java.util.Map;
import java.util.Set;

/**
 * Symbols library
 * Created by emmanuel on 15-05-21.
 */
public class IconShapeLib {
private static final String TAG = "SymbolsLib";
private Map<String, IconShape> ICONS = new HashMap<>();

public void setIcon(String name, IconShape iconShape) {
  if (getIcon(name) == null) {
    ICONS.put(name, iconShape);
  }
}

public IconShape getIcon(String name) {
  return ICONS.get(name);
}

/**
 * Utility fn list the keys for all entries
 */
public void listIcons() {
  final Set<String> strings = ICONS.keySet();
  for (String name : strings) {
    Log.i(TAG, name);
  }
}
}