package com.jibestream.jibestreamandroidlibrary.main;

import android.util.Log;

import com.jibestream.jibestreamandroidlibrary.shapes.MultiShape;

import java.util.HashMap;
import java.util.Map;
import java.util.Set;

/**
 * Symbols library
 * Created by emmanuel on 15-05-21.
 */
public class SymbolsLib {
private static final String TAG = "SymbolsLib";
private static Map<String, MultiShape> SYMBOLS = new HashMap<>();

public static void setSymbol(String name, MultiShape multiShape, boolean overideIfExists) {
  if (overideIfExists) {
    SYMBOLS.put(name, multiShape);
  } else {
    if (getSymbol(name) == null) {
      SYMBOLS.put(name, multiShape);
    }
  }

}

public static MultiShape getSymbol(String name) {
  return SYMBOLS.get(name);
}

/**
 * Utility fn list the keys for all entries
 */
public static void listSymbols() {
  final Set<String> strings = SYMBOLS.keySet();
  for (String name : strings) {
    Log.i(TAG, name);
  }
}
}