package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.astar;

/**
 * Created by emmanuel on 15-06-24.
 */
public class ASEdge {
public int id;
public int[] nodes;
public int type;
public float cost;
public int acc;
public float speed;
public int direction;

public ASEdge(int id, int[] nodeIds, int type, float cost, int acc, float speed, int direction) {
  this.id = id;
  this.nodes = nodeIds;
  this.type = type;
  this.cost = cost;
  this.acc = acc;
  this.speed = speed;
  this.direction = direction;
}
}
