package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.astar;

public class ASNode {
public int id;
public double x;
public double y;
public double z;
public int decisionPoint;
public int mapId;
public ASEdge[] edges;
public Neighbor[] neighbors;
public boolean visited;
public boolean closed;
public float f;
public float g;
public float h;
public ASNode parent;
public int usedEdgeTypeId;

public ASNode(int id, double x, double y, double z, int decisionPoint, int mapId, ASEdge[] edges, Neighbor[] neighbors) {
  this.id = id;
  this.x = x;
  this.y = y;
  this.z = z;
  this.decisionPoint = decisionPoint;
  this.mapId = mapId;
  this.edges = edges;
  this.neighbors = neighbors;
  init();
}

private void init() {
  f = 0;
  g = 0;
  h = 0;
  visited = false;
  closed = false;
  parent = null;
  usedEdgeTypeId = -1;
}

public void reset() {
  init();
}

public ASNode clone() {
  ASNode asNode = new ASNode(id, x, y, z, decisionPoint, mapId, edges.clone(), neighbors.clone());
  asNode.f = f;
  asNode.g = g;
  asNode.h = h;
  asNode.visited = visited;
  asNode.closed = closed;
  asNode.parent = parent;
  asNode.usedEdgeTypeId = usedEdgeTypeId;
  return asNode;
}
}
