package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.astar;


import com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects.Waypoint;

/**
 * Precalculated adjacent nodes
 * cost is the most important thing
 * Created by emmanuel on 15-06-24.
 */
public class Neighbor {
public int id;
public float cost;
public int acc;
public int edgeId;
public int edgeTypeId;
public float distance;
public double x;
public double y;
public float z;

public Neighbor(Waypoint currentWP, ASEdge currentEdge, float totalCost, float distance, float z) {
  id = currentWP.id;
  cost = totalCost;
  acc = currentEdge.acc;
  edgeId = currentEdge.id;
  edgeTypeId = currentEdge.type;
  this.distance = distance;
  x = currentWP.x;
  y = currentWP.y;
  this.z = z;
}
}
