package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 15-07-14.
 */
public class Amenity implements Parcelable {
public AmenityBean bean;
public Waypoint[] waypoints;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeParcelable(this.bean, 0);
  dest.writeParcelableArray(this.waypoints, 0);
}

public Amenity() {
}

protected Amenity(Parcel in) {
  this.bean = in.readParcelable(AmenityBean.class.getClassLoader());
  this.waypoints = (Waypoint[]) in.readParcelableArray(Waypoint.class.getClassLoader());
}

public static final Parcelable.Creator<Amenity> CREATOR = new Parcelable.Creator<Amenity>() {
  public Amenity createFromParcel(Parcel source) {
    return new Amenity(source);
  }

  public Amenity[] newArray(int size) {
    return new Amenity[size];
  }
};
}
