package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

public class Category implements Parcelable {
/**
 * Internal ID of a category
 */
public Integer id;
/**
 * Name of a category
 */
public String name;
/**
 * Keywords of a category.
 */
public String keywords;
/**
 * Sort order/Sequence of a category.
 */
public Integer sequence;
/**
 * Project ID to which category belongs.
 */
public Integer projectId;
/**
 * TypeID of a category.
 */
public Integer categoryType;
/**
 * Type name of a category.
 */
public String categoryTypeName;
/**
 * Client ID of a category.
 */
public String clientCategoryId;
/**
 * Name of a category, used for internal purpose
 */
public String text;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeValue(this.id);
  dest.writeString(this.name);
  dest.writeString(this.keywords);
  dest.writeValue(this.sequence);
  dest.writeValue(this.projectId);
  dest.writeValue(this.categoryType);
  dest.writeString(this.categoryTypeName);
  dest.writeString(this.clientCategoryId);
  dest.writeString(this.text);
}

public Category() {
}

protected Category(Parcel in) {
  this.id = (Integer) in.readValue(Integer.class.getClassLoader());
  this.name = in.readString();
  this.keywords = in.readString();
  this.sequence = (Integer) in.readValue(Integer.class.getClassLoader());
  this.projectId = (Integer) in.readValue(Integer.class.getClassLoader());
  this.categoryType = (Integer) in.readValue(Integer.class.getClassLoader());
  this.categoryTypeName = in.readString();
  this.clientCategoryId = in.readString();
  this.text = in.readString();
}

public static final Parcelable.Creator<Category> CREATOR = new Parcelable.Creator<Category>() {
  public Category createFromParcel(Parcel source) {
    return new Category(source);
  }

  public Category[] newArray(int size) {
    return new Category[size];
  }
};
}
