package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.graphics.Bitmap;
import android.os.Parcel;
import android.os.Parcelable;

import com.jibestream.jibestreamandroidlibrary.shapes.IconShape;
import com.jibestream.jibestreamandroidlibrary.shapes.MultiShape;

/**
 * Created by emmanuel on 15-07-15.
 */
public class DestinationLabel implements Parcelable {
public int componentId;
public int locationId;
public String uri;
public String label;
public String description;
public String localizedText;
public int mapId;
public int typeId;
public float locationX;
public float locationY;
public boolean ck;
public int projectId;
public String iconImagePath;
public String filePath;
public float rotation;
public int zoomlevel;
public String componentTypeName;
public String webPath;
// Added and populated from AndroidSDK
/**
 * The Bitmap of the field ccc
 */
//public Bitmap iconBitmap;
public String iconSVGDOM;
public String iconShapeLibKey;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.componentId);
  dest.writeInt(this.locationId);
  dest.writeString(this.uri);
  dest.writeString(this.label);
  dest.writeString(this.description);
  dest.writeString(this.localizedText);
  dest.writeInt(this.mapId);
  dest.writeInt(this.typeId);
  dest.writeFloat(this.locationX);
  dest.writeFloat(this.locationY);
  dest.writeByte(ck ? (byte) 1 : (byte) 0);
  dest.writeInt(this.projectId);
  dest.writeString(this.iconImagePath);
  dest.writeString(this.filePath);
  dest.writeFloat(this.rotation);
  dest.writeInt(this.zoomlevel);
  dest.writeString(this.componentTypeName);
  dest.writeString(this.webPath);
//  dest.writeParcelable(this.iconBitmap, 0);
  dest.writeString(this.iconSVGDOM);
}

public DestinationLabel() {
}

protected DestinationLabel(Parcel in) {
  this.componentId = in.readInt();
  this.locationId = in.readInt();
  this.uri = in.readString();
  this.label = in.readString();
  this.description = in.readString();
  this.localizedText = in.readString();
  this.mapId = in.readInt();
  this.typeId = in.readInt();
  this.locationX = in.readFloat();
  this.locationY = in.readFloat();
  this.ck = in.readByte() != 0;
  this.projectId = in.readInt();
  this.iconImagePath = in.readString();
  this.filePath = in.readString();
  this.rotation = in.readFloat();
  this.zoomlevel = in.readInt();
  this.componentTypeName = in.readString();
  this.webPath = in.readString();
//  this.iconBitmap = in.readParcelable(Bitmap.class.getClassLoader());
  this.iconSVGDOM = in.readString();
}

public static final Parcelable.Creator<DestinationLabel> CREATOR = new Parcelable.Creator<DestinationLabel>() {
  public DestinationLabel createFromParcel(Parcel source) {
    return new DestinationLabel(source);
  }

  public DestinationLabel[] newArray(int size) {
    return new DestinationLabel[size];
  }
};
}
