package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 2015-10-04.
 */
public class DestinationProximity implements Parcelable {
public String proximityType;
public int destinationId;
public String clientDestinationId;
public String destinationName;
public boolean onSameFloor;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeString(this.proximityType);
  dest.writeInt(this.destinationId);
  dest.writeString(this.clientDestinationId);
  dest.writeString(this.destinationName);
  dest.writeByte(onSameFloor ? (byte) 1 : (byte) 0);
}

public DestinationProximity() {
}

protected DestinationProximity(Parcel in) {
  this.proximityType = in.readString();
  this.destinationId = in.readInt();
  this.clientDestinationId = in.readString();
  this.destinationName = in.readString();
  this.onSameFloor = in.readByte() != 0;
}

public static final Parcelable.Creator<DestinationProximity> CREATOR = new Parcelable.Creator<DestinationProximity>() {
  public DestinationProximity createFromParcel(Parcel source) {
    return new DestinationProximity(source);
  }

  public DestinationProximity[] newArray(int size) {
    return new DestinationProximity[size];
  }
};
}