package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

public class GeoCoordinate implements Parcelable {

public float lat;
public float lng;

public GeoCoordinate(float lat, float lng) {
  super();
  this.lat = lat;
  this.lng = lng;
}

@Override
public String toString() {
  return "Coordinate [lat=" + lat + ", lng=" + lng + "]";
}


@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeFloat(this.lat);
  dest.writeFloat(this.lng);
}

protected GeoCoordinate(Parcel in) {
  this.lat = in.readFloat();
  this.lng = in.readFloat();
}

public static final Parcelable.Creator<GeoCoordinate> CREATOR = new Parcelable.Creator<GeoCoordinate>() {
  public GeoCoordinate createFromParcel(Parcel source) {
    return new GeoCoordinate(source);
  }

  public GeoCoordinate[] newArray(int size) {
    return new GeoCoordinate[size];
  }
};
}
