package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 15-06-25.
 */
public class Location implements Parcelable {
public int projectId;
public String name;
public int locationId;
public String locationName;
public String status;
public boolean ck;
public String clientProjectId;
public String[] languages;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.projectId);
  dest.writeString(this.name);
  dest.writeInt(this.locationId);
  dest.writeString(this.locationName);
  dest.writeString(this.status);
  dest.writeByte(ck ? (byte) 1 : (byte) 0);
  dest.writeString(this.clientProjectId);
  dest.writeStringArray(this.languages);
}

public Location() {
}

protected Location(Parcel in) {
  this.projectId = in.readInt();
  this.name = in.readString();
  this.locationId = in.readInt();
  this.locationName = in.readString();
  this.status = in.readString();
  this.ck = in.readByte() != 0;
  this.clientProjectId = in.readString();
  this.languages = in.createStringArray();
}

public static final Parcelable.Creator<Location> CREATOR = new Parcelable.Creator<Location>() {
  public Location createFromParcel(Parcel source) {
    return new Location(source);
  }

  public Location[] newArray(int size) {
    return new Location[size];
  }
};
}
