package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.graphics.RectF;
import android.os.Parcel;
import android.os.Parcelable;

import com.jibestream.jibestreamandroidlibrary.elements.Element;

import java.util.List;

/**
 * Map info accumulated
 */
public class MapFull implements Parcelable {
public Map map;
public String svg;
public MapLabel[] mapLabels;
public DestinationLabel[] destinationLabels;
public Waypoint[] waypoints;

public MapFull() {
}

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeParcelable(this.map, 0);
  dest.writeString(this.svg);
  dest.writeParcelableArray(this.mapLabels, 0);
  dest.writeParcelableArray(this.destinationLabels, 0);
  dest.writeParcelableArray(this.waypoints, 0);
}

protected MapFull(Parcel in) {
  this.map = in.readParcelable(Map.class.getClassLoader());
  this.svg = in.readString();
  this.mapLabels = (MapLabel[]) in.readParcelableArray(MapLabel.class.getClassLoader());
  this.destinationLabels = (DestinationLabel[]) in.readParcelableArray(DestinationLabel.class.getClassLoader());
  this.waypoints = (Waypoint[]) in.readParcelableArray(Waypoint.class.getClassLoader());
}

public static final Creator<MapFull> CREATOR = new Creator<MapFull>() {
  public MapFull createFromParcel(Parcel source) {
    return new MapFull(source);
  }

  public MapFull[] newArray(int size) {
    return new MapFull[size];
  }
};
}
