package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

import java.util.Arrays;

public class Path implements Parcelable {

public int id;
public int localId;
public int type;
public int weight;
public boolean defaultWeight;
public int direction;
public String name;
public int status;
public int[] waypoints;

public Path() {
}

public Path(int id, int localId, int type, int weight, boolean defaultWeight, int direction, String name, int status, int[] waypoints) {
  this.id = id;
  this.localId = localId;
  this.type = type;
  this.weight = weight;
  this.defaultWeight = defaultWeight;
  this.direction = direction;
  this.name = name;
  this.status = status;
  this.waypoints = waypoints;
}

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.id);
  dest.writeInt(this.localId);
  dest.writeInt(this.type);
  dest.writeInt(this.weight);
  dest.writeByte(defaultWeight ? (byte) 1 : (byte) 0);
  dest.writeInt(this.direction);
  dest.writeString(this.name);
  dest.writeInt(this.status);
  dest.writeIntArray(this.waypoints);
}

protected Path(Parcel in) {
  this.id = in.readInt();
  this.localId = in.readInt();
  this.type = in.readInt();
  this.weight = in.readInt();
  this.defaultWeight = in.readByte() != 0;
  this.direction = in.readInt();
  this.name = in.readString();
  this.status = in.readInt();
  this.waypoints = in.createIntArray();
}

public static final Parcelable.Creator<Path> CREATOR = new Parcelable.Creator<Path>() {
  public Path createFromParcel(Parcel source) {
    return new Path(source);
  }

  public Path[] newArray(int size) {
    return new Path[size];
  }
};

@Override
public String toString() {
  return "Path{" +
      "id=" + id +
      ", localId=" + localId +
      ", type=" + type +
      ", weight=" + weight +
      ", defaultWeight=" + defaultWeight +
      ", direction=" + direction +
      ", name='" + name + '\'' +
      ", status=" + status +
      ", waypoints=" + Arrays.toString(waypoints) +
      '}';
}
}
