package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.graphics.Bitmap;
import android.os.Parcel;
import android.os.Parcelable;

import com.jibestream.jibestreamandroidlibrary.shapes.IconShape;

import java.util.Arrays;

public class PathType implements Parcelable {
public final static int PATH_TYPE_NORMAL_PATH = 1;
public final static int PATH_TYPE_ELEVATOR = 2;
public final static int PATH_TYPE_ESCALATOR = 3;
public final static int PATH_TYPE_STAIR_CASE = 4;
public final static int PATH_TYPE_MOVING_WALKWAY = 5;
public int pathTypeId;
public String typeName;
public String description;
public int accessibility;
public int speed;
public int maxfloors;
public float weight;
public int direction;
public String metaData;
public Uri[] pathtypeUri;
public int projectId;
public int typeidPK;
// Added and populated from AndroidSDK
/**
 * The SVG representation
 */
public String svgDOM;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.pathTypeId);
  dest.writeString(this.typeName);
  dest.writeString(this.description);
  dest.writeInt(this.accessibility);
  dest.writeInt(this.speed);
  dest.writeInt(this.maxfloors);
  dest.writeFloat(this.weight);
  dest.writeInt(this.direction);
  dest.writeString(this.metaData);
  dest.writeParcelableArray(this.pathtypeUri, 0);
  dest.writeInt(this.projectId);
  dest.writeInt(this.typeidPK);
  dest.writeString(this.svgDOM);
}

public PathType() {
}

protected PathType(Parcel in) {
  this.pathTypeId = in.readInt();
  this.typeName = in.readString();
  this.description = in.readString();
  this.accessibility = in.readInt();
  this.speed = in.readInt();
  this.maxfloors = in.readInt();
  this.weight = in.readFloat();
  this.direction = in.readInt();
  this.metaData = in.readString();
  this.pathtypeUri = (Uri[]) in.readParcelableArray(Uri.class.getClassLoader());
  this.projectId = in.readInt();
  this.typeidPK = in.readInt();
  this.svgDOM = in.readString();
}

public static final Parcelable.Creator<PathType> CREATOR = new Parcelable.Creator<PathType>() {
  public PathType createFromParcel(Parcel source) {
    return new PathType(source);
  }

  public PathType[] newArray(int size) {
    return new PathType[size];
  }
};

@Override
public String toString() {
  return "PathType{" +
      "pathTypeId=" + pathTypeId +
      ", typeName='" + typeName + '\'' +
      ", description='" + description + '\'' +
      ", accessibility=" + accessibility +
      ", speed=" + speed +
      ", maxfloors=" + maxfloors +
      ", weight=" + weight +
      ", direction=" + direction +
      ", metaData='" + metaData + '\'' +
      ", pathtypeUri=" + Arrays.toString(pathtypeUri) +
      ", projectId=" + projectId +
      ", typeidPK=" + typeidPK +
      ", svgDOM='" + svgDOM + '\'' +
      '}';
}
}
