package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.graphics.Region;
import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by joshzoffranieri on 10/5/15.
 */
public class Refresh implements Parcelable {
public String action;
public String clientTimestamp;
public String componentType;
public String componentId;

public Refresh() {
}

public Refresh(String action, String clientTimestamp, String componentType, String componentId) {
  this.action = action;
  this.clientTimestamp = clientTimestamp;
  this.componentType = componentType;
  this.componentId = componentId;
}

public static boolean validate(Refresh refresh) {
  if (refresh == null) return false;
  if (refresh.clientTimestamp == null) return false;
  if (refresh.clientTimestamp.isEmpty()) return false;
  return true;
}

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeString(this.action);
  dest.writeString(this.clientTimestamp);
  dest.writeString(this.componentType);
  dest.writeString(this.componentId);
}

protected Refresh(Parcel in) {
  this.action = in.readString();
  this.clientTimestamp = in.readString();
  this.componentType = in.readString();
  this.componentId = in.readString();
}

public static final Parcelable.Creator<Refresh> CREATOR = new Parcelable.Creator<Refresh>() {
  public Refresh createFromParcel(Parcel source) {
    return new Refresh(source);
  }

  public Refresh[] newArray(int size) {
    return new Refresh[size];
  }
};
}
