package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

public class Uri implements Parcelable {
public int uriId;
public int uriTypeId;
public String uri;
public String filePath;
public String metaData;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.uriId);
  dest.writeInt(this.uriTypeId);
  dest.writeString(this.uri);
  dest.writeString(this.filePath);
  dest.writeString(this.metaData);
}

public Uri() {
}

protected Uri(Parcel in) {
  this.uriId = in.readInt();
  this.uriTypeId = in.readInt();
  this.uri = in.readString();
  this.filePath = in.readString();
  this.metaData = in.readString();
}

public static final Parcelable.Creator<Uri> CREATOR = new Parcelable.Creator<Uri>() {
  public Uri createFromParcel(Parcel source) {
    return new Uri(source);
  }

  public Uri[] newArray(int size) {
    return new Uri[size];
  }
};

@Override
public String toString() {
  return "Uri{" +
      "uriId=" + uriId +
      ", uriTypeId=" + uriTypeId +
      ", uri='" + uri + '\'' +
      ", filePath='" + filePath + '\'' +
      ", metaData='" + metaData + '\'' +
      '}';
}
}
