package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

public class Waypoint implements Parcelable {

public int id;
public int localId;
public int mapId;
public double x;
public double y;
public int status;
public WaypointEntityLink[] associations;
public int decisionPoint;
public int zoneId;
public Zone zone;
  public String unitNumber;

public Waypoint() {
}

public Waypoint(int id, int map, double xCoor, double yCoor, int status, String unitNumber) {
  this.id = id;
  this.mapId = map;
  this.x = xCoor;
  this.y = yCoor;
  this.status = status;
  this.unitNumber = unitNumber;
}


@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.id);
  dest.writeInt(this.localId);
  dest.writeInt(this.mapId);
  dest.writeDouble(this.x);
  dest.writeDouble(this.y);
  dest.writeInt(this.status);
  dest.writeParcelableArray(this.associations, 0);
  dest.writeInt(this.decisionPoint);
  dest.writeInt(this.zoneId);
  dest.writeParcelable(this.zone, 0);
  dest.writeString(this.unitNumber);
}

protected Waypoint(Parcel in) {
  this.id = in.readInt();
  this.localId = in.readInt();
  this.mapId = in.readInt();
  this.x = in.readDouble();
  this.y = in.readDouble();
  this.status = in.readInt();
  this.associations = (WaypointEntityLink[]) in.readParcelableArray(WaypointEntityLink.class.getClassLoader());
  this.decisionPoint = in.readInt();
  this.zoneId = in.readInt();
  this.zone = in.readParcelable(Zone.class.getClassLoader());
  this.unitNumber = in.readString();
}

public static final Creator<Waypoint> CREATOR = new Creator<Waypoint>() {
  public Waypoint createFromParcel(Parcel source) {
    return new Waypoint(source);
  }

  public Waypoint[] newArray(int size) {
    return new Waypoint[size];
  }
};
}
