package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;


import android.os.Parcel;
import android.os.Parcelable;

public class WaypointEntityLink implements java.io.Serializable, Parcelable {
/// Entity typesID
public static final int EntityTypeID_Destination = 1;
public static final int EntityTypeID_Device = 2;
public static final int EntityTypeID_Event = 19;
public static final int EntityTypeID_Amenity = 26;

/**
 * The Waypoint this waypointEntityLink belongs to
 */
public int waypointId;
/**
 * On of the above static types
 * Denotes what type of entity is associated with
 */
public int entityTypeId;
/**
 * The entityTypeId id
 * what id of its type is associated with
 */
public int entityId;

public int landmarkRating;

@Override
public String toString() {
  return "WaypointEntityLink{" +
      "waypointId=" + waypointId +
      ", entityTypeId=" + entityTypeId +
      ", entityId=" + entityId +
      ", landmarkRating=" + landmarkRating +
      '}';
}


@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.waypointId);
  dest.writeInt(this.entityTypeId);
  dest.writeInt(this.entityId);
  dest.writeInt(this.landmarkRating);
}

public WaypointEntityLink() {
}

protected WaypointEntityLink(Parcel in) {
  this.waypointId = in.readInt();
  this.entityTypeId = in.readInt();
  this.entityId = in.readInt();
  this.landmarkRating = in.readInt();
}

public static final Parcelable.Creator<WaypointEntityLink> CREATOR = new Parcelable.Creator<WaypointEntityLink>() {
  public WaypointEntityLink createFromParcel(Parcel source) {
    return new WaypointEntityLink(source);
  }

  public WaypointEntityLink[] newArray(int size) {
    return new WaypointEntityLink[size];
  }
};
}
