package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 2015-12-21.
 */
public class Zone implements Parcelable {
public int zoneId;
public String clientId;
public int statusCode;
public int projectId;
public ZoneDetails[] zoneDetails;
  public int typeId;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.zoneId);
  dest.writeString(this.clientId);
  dest.writeInt(this.statusCode);
  dest.writeInt(this.projectId);
  dest.writeParcelableArray(this.zoneDetails, 0);
  dest.writeInt(this.typeId);
}

public Zone() {
}

protected Zone(Parcel in) {
  this.zoneId = in.readInt();
  this.clientId = in.readString();
  this.statusCode = in.readInt();
  this.projectId = in.readInt();
  this.zoneDetails = (ZoneDetails[]) in.readParcelableArray(ZoneDetails.class.getClassLoader());
  this.typeId = in.readInt();
}

public static final Parcelable.Creator<Zone> CREATOR = new Parcelable.Creator<Zone>() {
  public Zone createFromParcel(Parcel source) {
    return new Zone(source);
  }

  public Zone[] newArray(int size) {
    return new Zone[size];
  }
};
}
