package com.jibestream.jibestreamandroidlibrary.mapBuilderV3.dataObjects;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 2015-12-21.
 */
public class ZoneDetails implements Parcelable {
public int zoneId;
public int languageId;
public String zoneName;
public String zoneDescription;
public int projectId;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeInt(this.zoneId);
  dest.writeInt(this.languageId);
  dest.writeString(this.zoneName);
  dest.writeString(this.zoneDescription);
  dest.writeInt(this.projectId);
}

public ZoneDetails() {
}

protected ZoneDetails(Parcel in) {
  this.zoneId = in.readInt();
  this.languageId = in.readInt();
  this.zoneName = in.readString();
  this.zoneDescription = in.readString();
  this.projectId = in.readInt();
}

public static final Parcelable.Creator<ZoneDetails> CREATOR = new Parcelable.Creator<ZoneDetails>() {
  public ZoneDetails createFromParcel(Parcel source) {
    return new ZoneDetails(source);
  }

  public ZoneDetails[] newArray(int size) {
    return new ZoneDetails[size];
  }
};
}
