package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Paint;
import android.graphics.RectF;

/**
 * Circle shape abstraction for easy canvas calls
 * Created by emmanuel on 15-05-13.
 */
public class Circle extends Shape {
private float radius;
private float x;
private float y;

public Circle() {
  this(10);
}

public Circle(float radius) {
  super();
  this.radius = radius;
  calc();
}

private void calc() {
  final RectF bbox = new RectF(-radius, -radius, radius, radius);
  bbox.offset(x, y);
  setBBox(bbox);
}

@Override
public void onDraw(Canvas canvas, Paint paint) {
  canvas.drawCircle(x, y, radius, paint);
}

public synchronized float getRadius() {
  return radius;
}

public synchronized void setRadius(float radius) {
  this.radius = radius;
  calc();
}

public synchronized float getX() {
  return x;
}

public synchronized void setX(float x) {
  this.x = x;
  calc();
}

public synchronized float getY() {
  return y;
}

public synchronized void setY(float y) {
  this.y = y;
  calc();
}

}
