package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Paint;

/**
 * Created by emmanuel on 15-05-11.
 */
public interface IDraw {
/**
 * Interface called from engine when rendering takes place
 * It may called couple of times in the case of a touch event.
 *
 */

/**
 * Must contain a Canvas draw method if onDraw fn isnt
 * overridden.
 * Really a convenience method in order to avoid repeatably
 * typing the canvas draw method eg canvas.drawRect(...
 * in the onDraw method.
 *
 */
void onDraw(Canvas canvas, Paint paint);


}
