package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Paint;
import android.graphics.RectF;

/**
 * Line Shape abstraction for easy canvas calls
 * Created by emmanuel on 15-05-14.
 */
public class Line extends Shape {
private float startX;
private float startY;
private float stopX;
private float stopY;

public Line(float startX) {
  this.startX = startX;
  calc();
}

public Line() {
  this(0, 0, 0, 0);
}

public Line(float startX, float startY, float stopX, float stopY) {
  super();
  this.startX = startX;
  this.startY = startY;
  this.stopX = stopX;
  this.stopY = stopY;
  calc();
}

private void calc() {
  RectF bbox = new RectF(startX, startY, stopX, stopY);
  setBBox(bbox);
}

@Override
public void onDraw(Canvas canvas, Paint paint) {
  canvas.drawLine(startX, startY, stopX, stopY, paint);
}


public synchronized float getStartX() {
  return startX;
}

public synchronized void setStartX(float startX) {
  this.startX = startX;
  calc();
}


public synchronized float getStartY() {
  return startY;
}

public synchronized void setStartY(float startY) {
  this.startY = startY;
  calc();
}


public synchronized float getStopY() {
  return stopY;
}

public synchronized void setStopY(float stopY) {
  this.stopY = stopY;
  calc();
}

public synchronized float getStopX() {
  return stopX;
}

public synchronized void setStopX(float stopX) {
  this.stopX = stopX;
  calc();
}

}
