package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Paint;
import android.graphics.RectF;

/**
 * Circle shape abstraction for easy canvas calls
 * Created by emmanuel on 15-05-13.
 */
public class Oval extends Shape {
private RectF rectF;

public Oval() {
  this(new RectF(-5, -2, 5, 2));
}

public Oval(RectF rectF) {
  super();
  this.rectF = rectF;
  calc();
}

private void calc() {
  setBBox(rectF);
}

@Override
public void onDraw(Canvas canvas, Paint paint) {
  canvas.drawOval(rectF, paint);
}

public synchronized RectF getRectF() {
  return rectF;
}

public synchronized void setRectF(RectF rectF) {
  this.rectF = rectF;
  calc();
}
}
