package com.jibestream.jibestreamandroidlibrary.shapes;

/**
 * Square shape abstraction easy canvas calls
 * Created by emmanuel on 15-05-14.
 */
public class Square extends Rectangle {
public synchronized float getSize() {
  return size;
}

public synchronized void setSize(float size) {
  this.size = size;
  init();
}

private float size;

public Square() {
  this(10);
}

public Square(float size) {
  super(0, 0, size, size);
  this.size = size;
  init();
}

private void init() {
  float h = size * 0.5f;
  super.setLeft(-h);
  super.setTop(-h);
  super.setRight(h);
  super.setBottom(h);
}
}
