package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.graphics.Rect;
import android.graphics.RectF;
import android.text.Layout;
import android.text.StaticLayout;
import android.text.TextPaint;

import com.jibestream.jibestreamandroidlibrary.styles.CssStyles;

/**
 * Created by emmanuel on 15-09-03.
 */
public class TextStaticLayout extends Shape implements IShapeText {
private StaticLayout staticLayout;
private TextPaint textPaint;
private String textString = "Text";
private Layout.Alignment align = Layout.Alignment.ALIGN_NORMAL;
private int width = 100;

public TextStaticLayout() {
  textPaint = new TextPaint();
  textPaint.setColor(Color.WHITE);
  calc();
}

private void calc() {
  staticLayout = new StaticLayout(textString, textPaint, width, align, 1, 0, true);
  RectF bbox = new RectF(0, 0, staticLayout.getWidth(), staticLayout.getHeight());
  setBBox(bbox);
}

@Override
public void onDraw(Canvas canvas, Paint paint) {
  staticLayout.draw(canvas);
}

public synchronized String getTextString() {
  return textString;
}

public synchronized void setTextString(String textString) {
  this.textString = textString;
  calc();
}

public synchronized TextPaint getTextPaint() {
  return textPaint;
}

public synchronized void setTextPaint(TextPaint textPaint) {
  this.textPaint = textPaint;
  calc();
}

public synchronized int getWidth() {
  return width;
}

public synchronized void setWidth(int width) {
  this.width = width;
  calc();
}

public float getHeight() {
  return staticLayout.getHeight();
}


}
