package com.jibestream.jibestreamandroidlibrary.shapes;

import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Paint;
import android.graphics.RectF;

/**
 * Twodent shape abstraction for easy canvas calls
 * Created by emmanuel on 15-05-13.
 */
public class TwoDent extends Shape {
private static final Paint pR, pG, pB;

static {
  pR = new Paint();
  pR.setStyle(Paint.Style.STROKE);
  pR.setColor(Color.RED);
  pR.setStrokeWidth(1);
  pG = new Paint();
  pG.setStyle(Paint.Style.STROKE);
  pG.setColor(Color.GREEN);
  pG.setStrokeWidth(1);
  pB = new Paint();
  pB.setStyle(Paint.Style.STROKE);
  pB.setColor(Color.BLUE);
  pB.setStrokeWidth(1);

}

public TwoDent() {
  super();
  RectF bbox = new RectF(-5, -5, 5, 5);
  setBBox(bbox);
}

@Override
public void onDraw(Canvas canvas, Paint paint) {
  canvas.drawCircle(0, 0, 5, pB);
  canvas.drawLine(0, 0, 0, 5, pG);
  canvas.drawLine(0, 0, 5, 0, pR);
}
}
