package com.jibestream.jibestreamandroidlibrary.styles;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 15-08-21.
 */
public class PaintFillDO implements Parcelable {

public String color;
public float alpha;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeString(this.color);
  dest.writeFloat(this.alpha);
}

public PaintFillDO() {
}

protected PaintFillDO(Parcel in) {
  this.color = in.readString();
  this.alpha = in.readFloat();
}

public static final Parcelable.Creator<PaintFillDO> CREATOR = new Parcelable.Creator<PaintFillDO>() {
  public PaintFillDO createFromParcel(Parcel source) {
    return new PaintFillDO(source);
  }

  public PaintFillDO[] newArray(int size) {
    return new PaintFillDO[size];
  }
};
}
