package com.jibestream.jibestreamandroidlibrary.styles;

import android.os.Parcel;
import android.os.Parcelable;

/**
 * Created by emmanuel on 15-08-21.
 */
public class PaintStrokeDO implements Parcelable {

public String color;
public float alpha;
public float strokeWidth;

@Override
public int describeContents() {
  return 0;
}

@Override
public void writeToParcel(Parcel dest, int flags) {
  dest.writeString(this.color);
  dest.writeFloat(this.alpha);
  dest.writeFloat(this.strokeWidth);
}

public PaintStrokeDO() {
}

protected PaintStrokeDO(Parcel in) {
  this.color = in.readString();
  this.alpha = in.readFloat();
  this.strokeWidth = in.readFloat();
}

public static final Parcelable.Creator<PaintStrokeDO> CREATOR = new Parcelable.Creator<PaintStrokeDO>() {
  public PaintStrokeDO createFromParcel(Parcel source) {
    return new PaintStrokeDO(source);
  }

  public PaintStrokeDO[] newArray(int size) {
    return new PaintStrokeDO[size];
  }
};
}
